@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here.
All colors MUST be HSL.
The @apply directive should not be used with the group utility class.
*/

@layer base {
  :root {
    /* Professional Business Color Palette */
    --background: 0 0% 100%;
    --foreground: 222 47% 11%;

    --primary: 221 83% 53%;
    --primary-foreground: 0 0% 100%;

    --secondary: 210 40% 96%;
    --secondary-foreground: 222 47% 11%;

    --accent: 210 40% 92%;
    --accent-foreground: 222 47% 11%;

    --muted: 210 40% 96%;
    --muted-foreground: 215 16% 47%;

    --card: 0 0% 100%;
    --card-foreground: 222 47% 11%;

    --border: 214 32% 91%;
    --input: 214 32% 91%;
    --ring: 221 83% 53%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --radius: 0.5rem;

    /* Business specific colors */
    --business-dark: 222 47% 11%;
    --business-blue: 221 83% 53%;
    --business-light: 210 40% 98%;
    --business-gray: 215 16% 47%;
  }

  .dark {
    --background: 222 47% 11%;
    --foreground: 210 40% 98%;

    --primary: 221 83% 53%;
    --primary-foreground: 0 0% 100%;

    --secondary: 217 33% 17%;
    --secondary-foreground: 210 40% 98%;

    --accent: 217 33% 17%;
    --accent-foreground: 210 40% 98%;

    --muted: 217 33% 17%;
    --muted-foreground: 215 20% 65%;

    --card: 222 47% 11%;
    --card-foreground: 210 40% 98%;

    --border: 217 33% 17%;
    --input: 217 33% 17%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

import { useState } from 'react';
import { Menu, X } from 'lucide-react';

const Navigation = () => {
  const [isOpen, setIsOpen] = useState(false);

  const navItems = [
    { name: '首页', href: '#home' },
    { name: '服务', href: '#services' },
    { name: '优势', href: '#advantages' },
    { name: '客户评价', href: '#testimonials' },
    { name: '关于我们', href: '#about' },
  ];

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-background/80 backdrop-blur-md border-b border-border">
      <div className="container mx-auto px-6 lg:px-8">
        <div className="flex items-center justify-between h-20">
          {/* Logo */}
          <div className="flex-shrink-0">
            <h1 className="text-2xl font-bold text-primary">企业品牌</h1>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => (
              <a
                key={item.name}
                href={item.href}
                className="text-foreground hover:text-primary transition-colors duration-200 font-medium"
              >
                {item.name}
              </a>
            ))}
            <button className="bg-primary text-primary-foreground px-6 py-2.5 rounded-lg hover:opacity-90 transition-opacity font-medium">
              联系我们
            </button>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-foreground hover:text-primary"
            >
              {isOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isOpen && (
          <div className="md:hidden pb-6">
            <div className="flex flex-col space-y-4">
              {navItems.map((item) => (
                <a
                  key={item.name}
                  href={item.href}
                  className="text-foreground hover:text-primary transition-colors duration-200 font-medium"
                  onClick={() => setIsOpen(false)}
                >
                  {item.name}
                </a>
              ))}
              <button className="bg-primary text-primary-foreground px-6 py-2.5 rounded-lg hover:opacity-90 transition-opacity font-medium">
                联系我们
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navigation;

import { Laptop, Target, TrendingUp, Shield, Zap, Users } from 'lucide-react';

const Services = () => {
  const services = [
    {
      icon: Laptop,
      title: '数字化转型',
      description: '为企业提供全方位的数字化转型咨询与实施服务，助力业务创新升级。',
    },
    {
      icon: Target,
      title: '战略咨询',
      description: '深度分析市场趋势，制定精准战略规划，助力企业把握发展机遇。',
    },
    {
      icon: TrendingUp,
      title: '业务增长',
      description: '通过数据驱动的增长策略，帮助企业实现可持续的业务增长。',
    },
    {
      icon: Shield,
      title: '风险管理',
      description: '建立完善的风险管理体系，保障企业稳健运营和长期发展。',
    },
    {
      icon: Zap,
      title: '技术创新',
      description: '运用前沿技术解决方案，提升企业核心竞争力和创新能力。',
    },
    {
      icon: Users,
      title: '团队赋能',
      description: '提供专业培训和咨询服务，全面提升团队能力和组织效能。',
    },
  ];

  return (
    <section id="services" className="py-20 lg:py-32 bg-background">
      <div className="container mx-auto px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center max-w-3xl mx-auto mb-16">
          <h2 className="text-3xl lg:text-5xl font-bold text-foreground mb-6">
            专业服务
          </h2>
          <p className="text-lg text-muted-foreground">
            我们提供全方位的企业服务解决方案，帮助您的企业在竞争中脱颖而出
          </p>
        </div>

        {/* Services Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => {
            const Icon = service.icon;
            return (
              <div
                key={index}
                className="group bg-card border border-border rounded-xl p-8 hover:shadow-lg transition-all duration-300 hover:-translate-y-1"
              >
                <div className="bg-primary/10 w-14 h-14 rounded-lg flex items-center justify-center mb-6 group-hover:bg-primary transition-colors">
                  <Icon className="text-primary group-hover:text-primary-foreground transition-colors" size={28} />
                </div>
                <h3 className="text-xl font-bold text-foreground mb-4">
                  {service.title}
                </h3>
                <p className="text-muted-foreground leading-relaxed">
                  {service.description}
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Services;

import { Award, Globe, Clock, HeartHandshake } from 'lucide-react';

const Advantages = () => {
  const advantages = [
    {
      icon: Award,
      title: '专业团队',
      description: '汇聚行业精英，拥有丰富的实战经验和专业资质',
      stats: '200+',
      statsLabel: '专业顾问',
    },
    {
      icon: Globe,
      title: '全球视野',
      description: '国际化服务能力，服务网络覆盖全球主要市场',
      stats: '50+',
      statsLabel: '服务城市',
    },
    {
      icon: Clock,
      title: '高效执行',
      description: '敏捷的项目管理体系，确保项目按时高质量交付',
      stats: '95%',
      statsLabel: '准时交付率',
    },
    {
      icon: HeartHandshake,
